New-Item -ItemType File -Name "main.cpp"
Set-Content -Path "main.cpp" -Value @"
#include<iostream>
using namespace std;

int main() {
    int n = 10;
    int fibo[n];
    fibo[0] = 0;
    fibo[1] = 1;

    for (int i = 2; i < n; i++) {
        fibo[i] = fibo[i-1] + fibo[i-2];
    }

    for (int i = 0; i < n; i++) {
        cout << fibo[i] << " ";
    }

    return 0;
}
"@

$folders = "folder1", "folder2"
$files = ("demo1.cpp", "demo2.cpp")

foreach ($folder in $folders) {
    New-Item -ItemType Directory -Name $folder
}

foreach ($file in $files) {
    New-Item -ItemType File -Name $file
}

